mapper.select:
select
access_records_id, employee_id, accessories_id, variation, access_date
from access_ecords


        and access_records_id = #{accessRecordsId}


        and employee_id = #{employeeId}


        and accessories_id = #{accessoriesId}


        and variation = #{variation}


        and access_date = #{accessDate}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from access_ecords


        and access_records_id = #{accessRecordsId}


        and employee_id = #{employeeId}


        and accessories_id = #{accessoriesId}


        and variation = #{variation}


        and access_date = #{accessDate}
