mapper.select:
select
type_id, type, type_function, type_description
from accessories_type


        and type_id = #{typeId}


        and type = #{type}


        and type_function = #{typeFunction}


        and type_description = #{typeDescription}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from accessories_type


        and type_id = #{typeId}


        and type = #{type}


        and type_function = #{typeFunction}


        and type_description = #{typeDescription}
