mapper.select:
select
file_id, file_place, records_id, file_type
from file


        and file_id = #{fileId}


        and file_place = #{filePlace}


        and records_id = #{recordsId}


        and file_type = #{fileType}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from file


        and file_id = #{fileId}


        and file_place = #{filePlace}


        and records_id = #{recordsId}


        and file_type = #{fileType}
