mapper.select:
select
file_id, records_id, file_place, file_type, file_size
from file


        and file_id = #{fileId}


        and records_id = #{recordsId}


        and file_place = #{filePlace}


        and file_type = #{fileType}


        and file_size = #{fileSize}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from file


        and file_id = #{fileId}


        and records_id = #{recordsId}


        and file_place = #{filePlace}


        and file_type = #{fileType}


        and file_size = #{fileSize}
