mapper.select:
select
records_id, maintenance_time, machine_id, employee_id, method_description, fault_description, word_place,
photo_place, vido_place, machine_type_id
from maintenance_records


        and records_id = #{recordsId}


        and maintenance_time = #{maintenanceTime}


        and machine_id = #{machineId}


        and employee_id = #{employeeId}


        and method_description = #{methodDescription}


        and fault_description = #{faultDescription}


        and word_place = #{wordPlace}


        and photo_place = #{photoPlace}


        and vido_place = #{vidoPlace}


        and machine_type_id = #{machineTypeId}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from maintenance_records


        and records_id = #{recordsId}


        and maintenance_time = #{maintenanceTime}


        and machine_id = #{machineId}


        and employee_id = #{employeeId}


        and method_description = #{methodDescription}


        and fault_description = #{faultDescription}


        and word_place = #{wordPlace}


        and photo_place = #{photoPlace}


        and vido_place = #{vidoPlace}


        and machine_type_id = #{machineTypeId}
